import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const MyCalendar = () => {
  const [date, setDate] = useState(new Date());
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(false);

  const onChange = async (newDate) => {
    setDate(newDate);
    setLoading(true);

    try {
      const selectedDate = newDate.toISOString().split('T')[0]; // Format date as YYYY-MM-DD
      const response = await fetch(`/api/makebooking/salonTable/${selectedDate}`);
      const fetchedBookings = await response.json();
      setBookings(fetchedBookings);
    } catch (err) {
      console.error('Error fetching bookings:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Calendar Component</h2>
      <Calendar onChange={onChange} value={date} />

      {/* Display fetched bookings for the selected date */}
      {loading && <p>Loading...</p>}
      {bookings.length > 0 && (
        <div>
          <h3>Bookings for {date.toLocaleDateString()}:</h3>
          <ul>
            {bookings.map((booking) => (
              <li key={booking._id}>
                {/* Display booking details (e.g., time, name) */}
                {booking.time} - {booking.name}
              </li>
            ))}
          </ul>
        </div>
      )}
      {bookings.length === 0 && !loading && (
        <p>No bookings for {date.toLocaleDateString()}</p>
      )}
    </div>
  );
};

export default MyCalendar;
